<!-- abbiamo usato il *ngFor come funzione for-each-->
<!-- {{ drink.idDrink }} <br />
        {{ drink.strDrink }} <br />
        {{ drink.strDrinkThumb }}<br /> -->
<!-- <img width="50" height="50" [src]="drink.strDrinkThumb" /> -->
<!-- [src] = "drink" avviene questo => assegna il valore che c'è a destra alla variabile che è a sinistra-->

<!-- Sezione navbar -->
<navbar class="navbar">
  <div class="container">
    <div class="row">
      <div class="col-6">
        <!-- Logo-->
        <h2>TheCocktailDB</h2>
      </div>
      <div class="col-6 flex-end">
        <button type="button" class="btn btn-light navbar-component">
          HOME
        </button>
        <button type="button" class="btn btn-light navbar">API</button>
      </div>
    </div>
  </div>
</navbar>
<!-- Sezione Lista Cocktail-->
<div class="container">
  <div class="row justify-content-center">
    <ul>
      <li *ngFor="let drink of drinks; center">
        <div class="col-4">
          <div class="card m-3" style="width: 9rem">
            <img
              [src]="drink.strDrinkThumb"
              class="card-img-top img-fluid"
              alt="..."
            />
            <div class="card-body">
              <p>{{ drink.strInstructionsIT }}</p>
            </div>
          </div>
        </div>
      </li>

      <hr />
    </ul>
  </div>
</div>

