  href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css"
  rel="stylesheet"
  integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN"
  crossorigin="anonymous"
/>
<div class="containerTot">
  <!-- Sezione navbar -->
  <nav class="navbar">
    <div class="container flex-row">
      <div class="col-6">
        <h2>TheCocktailDB</h2>
      </div>
      <div class="col-6">
        <div class="row flex-direction-end">
          <div class="col-4 p-0">
            <button type="button" class="btn btn-light navbar-component">
              HOME
            </button>
          </div>
          <div class="col-4 p-0">
            <button type="button" class="btn btn-light navbar-component">
              API
            </button>
          </div>
          <div class="col-4 p-0">
            <p>Search</p>
          </div>
        </div>
      </div>
    </div>
  </nav>
  <!-- Sezione Lista Cocktail -->
  <div class="container">
    <table>
      <dl>
        <div class="row justify-content-center">
          <dt *ngFor="let drink of drinks" class="col-4">
            <div class="card m-3" style="width: 9rem">
              <img
                [src]="drink.strDrinkThumb"
                class="card-img-top img-fluid"
                [alt]="drink.strDrink"
              />
              <div class="card-body">
                <p>{{ drink.strDrink }}</p>
              </div>
            </div>
          </dt>
        </div>
        <hr />
      </dl>
    </table>
  </div>
  <!-- Sezione Alfabeto -->
  <div class="container">
    <div class="row">
      <a routerLink="/home/a">A</a> / <a routerLink="/home/b">B</a> /
      <a routerLink="/home/c">C</a> / <a routerLink="/home/d">D</a> /
      <a routerLink="/home/e">E</a> / <a routerLink="/home/f">F</a> /
