  <div class="container">
    <div class="col-6">
      <div class="row flex-row">
        <a routerLink="/home">
          <img
            src="../../assets/logo.png"
            class="card-img-top img-fluid"
            alt="Logo"
          />
        </a>
      </div>
    </div>
    <div class="col-6">
      <div class="row flex-row">
        <div class="col-4 p-0">
          <a routerLink="/home">
            <button type="button" class="btn btn-light navbar-component">
              HOME
            </button>
          </a>
        </div>
      </div>
    </div>
  </div>
  <hr />
</nav>
