import { HttpClient } from "@angular/common/http";
import { Component, OnInit } from "@angular/core";
import { HttpClientModule } from "@angular/common/http";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  // styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  // Nuova parte,Utilizzo servizi http
  // -------------------------------------------------------------------------------
  // non lasciare mai la tipizzazione 'any', deve essere di tipizzazione 'Fatture[]' che è un interfaccia di tipizzazione
  drinks: any = [];
  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.http
      .get("https://www.thecocktaildb.com/api/json/v1/1/search.php?f=a")
      .subscribe((response: any) => {
        // this.fatture = response;
        console.log(response);
        this.drinks = response.drinks;
      });
  }
  delete(idFattura: number): void {
    this.http
      .delete("https://www.pippi.it/fattura?id= " + idFattura)
      .subscribe((response) => {
        // ...
      });
  }
}

