  </div>
  <!-- Sezione Ricerca -->
  <div class="container">
    <div class="row p-2">
      <div class="col-6">
        <p>Lat:</p>
      </div>
      <div class="col-6">
        <input
          class="form-control me-2"
          type="number"
          placeholder="0"
          aria-label="Search"
        />
      </div>
    </div>
    <div class="row p-2">
      <div class="col-6">
        <p>Lon:</p>
      </div>
      <div class="col-6">
        <input
          class="form-control me-2"
          type="number"
          placeholder="0"
          aria-label="Search"
