import { Component, Input, OnInit } from "@angular/core";
import { HttpClientModule } from "@angular/common/http";
import Drink from "../_models/drink.model";
import { HttpClient } from "@angular/common/http";
import { DrinkService } from "../_services/meteo.service";
import {
  ActivatedRoute,
  ParamMap,
  RouterModule,
  Routes,
} from "@angular/router";

@Component({
  selector: "app-drink-card",
  templateUrl: "./drink-card.component.html",
  styleUrls: ["./drink-card.component.css"],
})
export class DrinkCardComponent implements OnInit {
  constructor() {}
  @Input()
  drinkService!: DrinkService;
  drink!: Drink; // con il '!=' significa che l'inizializzazione della variabile è lasciata a noi e che Angular non se ne deve preoccupare
  letter: string = "a";
  ngOnInit(): void {
    //debugger per non avere il problema di dichiarazione variabile senza l'assegnazione
    if (!this.drink) {
      alert("Errore! non hai passato un drink"); //si poteva mettere l'iniziallizazione nel construttore
    }
  }
}

