  <li *ngFor="let drink of drinks">
    <!-- abbiamo usato il *ngFor come funzione for-each-->
    {{ drink.idDrink }} <br />
    {{ drink.strDrink }} <br />
    {{ drink.strDrinkThumb }}
    <img [src]="drink.strDrinkThumb" />
