  crossorigin="anonymous"
/>
<div class="containerTot">
  <!-- Sezione Titolo -->
  <div class="container mb-2 d-flex">
    <div class="col-6 text-center">Meteo</div>
    <div class="col-6 text-center">Foto Meteo</div>
  </div>
  <!-- Sezione Form -->
  <div class="container">
    <form name="ngForm" *ngSubmit="SearchCoords()"></form>
    <!-- Sezione Lat -->
    <div class="row p-2">
      <div class="col-6">
        <p>Lat:</p>
      </div>
      <div class="col-6">
        <input
          class="form-control me-2"
          [(ngModel)]="lat"
          type="number"
          placeholder="0"
          aria-label="Search"
        />
      </div>
    </div>
    <!-- Sezione Lon -->
    <div class="row p-2">
      <div class="col-6">
        <p>Lon:</p>
      </div>
      <div class="col-6">
        <input
          class="form-control me-2"
          [(ngModel)]="lon"
          type="number"
          placeholder="0"
          aria-label="Search"
        />
      </div>
    </div>
    <!-- Sezione Bottone Submit-->
    <div class="row p-2">
      <div class="col-12">
        <input type="submit" class="btn btn-dark" value="Submit Button" />
      </div>
    </div>
  </div>
  <!-- Sezione Città Predefinite -->
  <div class="containter">
    <div class="row">
      <div class="col-6">
        <a routerLink="/page-detail/lat=45°04'24/lon=7°40'32"><p>Torino</p></a>
      </div>
      <div class="col-6"><p>Milano</p></div>
    </div>
  </div>
</div>
