import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class ApiService {
  lon: string = "";
  lat: string = "";
  constructor(private http: HttpClient) {}
  //corretta maniera di inizializzare e usare le APi visto che potrebbero essere molte lon=113.2&lat=23.1&ac=0&unit=metric&output=json&tzshift=0
  baseUrl = "https://www.7timer.info/bin/astro.php?";
  searchByF() {
    return this.http.get(this.baseUrl + "lon=" + this.lon + "&lat=" + this.lat);
  }
  getInfoDrink(id: number) {
    console.log(id);
    return this.http.get(this.baseUrl + "lookup.php?i=" + id);
  }
}
