<!-- abbiamo usato il *ngFor come funzione for-each-->
<!-- {{ drink.idDrink }} <br />
        {{ drink.strDrink }} <br />
        {{ drink.strDrinkThumb }}<br /> -->
<!-- <img width="50" height="50" [src]="drink.strDrinkThumb" /> -->
<!-- [src] = "drink" avviene questo => assegna il valore che c'è a destra alla variabile che è a sinistra-->
<div class="container">
  <div class="row center">
    <ul>
      <li *ngFor="let drink of drinks">
        <div class="col-4">
          <div class="card m-3" style="width: 18rem">
            <img
              [src]="drink.strDrinkThumb"
              class="card-img-top img-fluid"
              alt="..."
            />
            <div class="card-body">
              <p>{{ drink.strInstructionsIT }}</p>
            </div>
          </div>
        </div>
      </li>

      <hr />
    </ul>
  </div>
</div>

