import { HttpClient } from "@angular/common/http";
import { Component, OnInit } from "@angular/core";

interface Cliente {
  nome: string;
  cognome: string;
  altezza?: number; //altezza?:number => opzionale nella dichiarazione della variabile 'jsonIn',può essere omesso
  indirizzo?: {
    via: string;
    numero: string;
    cap: number;
    citta: string;
  }[];
  email: string;
  sesso: "M" | "F" | "Altro";
  dataNascita?: number;
} // dentro 'Interface c'è solo la tippizzazione delle variabile,non inizializiamo o scriviamo funzioni dentro
@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  // styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  datiAnagrafici = true;
  percorsoStudi = false;
  esperienzeLavorative = false;

  nome = "";
  altro = "";

  jsonIn: Cliente = {
    nome: "",
    cognome: "",
    indirizzo: [],
    email: "",
    sesso: "Altro",
  };
  constructor(private http: HttpClient) {}

  ngOnInit(): void {}
  onDataNascitaChange() {}

