import { HttpClient } from "@angular/common/http";
import { Injectable } from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class ApiService {
  constructor(private http: HttpClient) {}
  //corretta maniera di inizializzare e usare le APi visto che potrebbero essere molte lon=113.2&lat=23.1&ac=0&unit=metric&output=json&tzshift=0
  coordInfo = "https://www.7timer.info/bin/astro.php?";
  searchByCoords(lon: string, lat: string) {
    return this.http.get(
      this.coordInfo +
        "lon=" +
        lon +
        "&lat=" +
        lat +
        "&ac=0&unit=metric&output=json&tzshift=0"
    );
  }
  getInfoDrink(id: number) {
    console.log(id);
    return this.http.get(this.coordInfo + "lookup.php?i=" + id);
  }
}
